jQuery(document).ready(function(){jQuery("#term-list-inner a").click(function(a){a.preventDefault();addTerm(this.innerHTML,"renameterm_old");addTerm(this.innerHTML,"deleteterm_name");addTerm(this.innerHTML,"addterm_match");addTerm(this.innerHTML,"termname_match");return false;});});function addTerm(b,c){var d=document.getElementById(c);if(d.value.length>0&&!d.value.match(/,\s*$/)){d.value+=", ";}var a=new RegExp(b+",");if(!d.value.match(a)){d.value+=b+", ";}return true;}